import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const userSlice = createSlice({
  name: "user",
  initialState: {
    apiKey: "",
    level: 0,
    kanjiIds: [],
  },
  reducers: {
    setApiKey: (state, action) => {
      state.apiKey = action.payload;
    },
    setLevel: (state, action) => {
      state.level = action.payload;
    },
  },
});

export const { setApiKey, setLevel } = userSlice.actions;

export const initStudyByLevel = (selectedLevels: string) => {
  return async (dispatch: any, getState: any) => {
    // const apiKey = getState().user.apiKey;
    // const kanjiIds = await getKanjiIdsByLevel(selectedLevels, apiKey);
    // dispatch(setKanjiIds(kanjiIds));
    await axios
      .get(`https://api.wanikani.com/v2/assignments?levels=${selectedLevels}`, {
        headers: {
          Authorization: `Bearer ${getState().user.apiKey}`,
        },
      })
      .then((res) => {
        // return res.data.data.map((assignment: any) => assignment.subject_id);
        console.log(res.data);
      });
  };
};

export default userSlice.reducer;
// holds api token, user stats (accuracy, etc), user settings (theme, etc), user account info (level, etc)
