import axios from "axios";
import React, { useState } from "react";
import { FC } from "react";
import { useDispatch } from "react-redux";
import { setApiKey } from "../reducers/UserReducer";

interface WelcocmeProps {
  hide: () => void;
}

export const Welcome: FC<WelcocmeProps> = ({ hide }) => {
  const [key, setKey] = useState("");
  const [showErrorText, setShowErrorText] = useState(false);

  const dispatch = useDispatch();

  const handleError = () => {
    setShowErrorText(true);

    setTimeout(() => {
      setShowErrorText(false);
    }, 5000);
  };

  const handleSubmit = () => {
    axios
      .get("https://api.wanikani.com/v2/user", {
        headers: {
          Authorization: `Bearer ${key}`,
        },
      })
      .then((res) => {
        if (res.status === 200) {
          console.log("valid api key", res.data);
          dispatch(setApiKey(key));
          localStorage.setItem("apiKey", JSON.stringify({ apiKey: key }));
          hide();
        }
      })
      .catch((err) => {
        console.log("invalid api key", err);
        handleError();
      });
  };

  const getKeyFromClipboard = () => {
    navigator.clipboard.readText().then((text) => {
      setKey(text);
    });
  };
  // className="phone:text-7xl tablet:text-8xl bg-gradient-to-r from-blue to-pink bg-clip-text text-transparent inline-block mb-5"

  return (
    <div className="bg-background w-full h-screen flex flex-col justify-center items-center font-body text-text">
      {/* <h1 className="phone:text-7xl tablet:text-8xl font-bold bg-gradient-to-r from-blue to-pink inline-block text-transparent bg-clip-text">
        wk similar
      </h1> */}
      <p className="phone:text-7xl tablet:text-8xl font-bold mb-5 text-center phone:w-2/3">
        wk similar
      </p>
      <div className="flex justify-center items-center mb-2">
        <label>
          <div className="label -mb-1">
            <span className="label-text text-lg">enter api token:</span>
          </div>
          <input
            type="text"
            value={key}
            onChange={(e) => setKey(e.target.value)}
            className={"input input-bordered"}
          />
        </label>
        <button
          onClick={getKeyFromClipboard}
          className="btn mt-10 ml-2 text-sm  text-center border bg-blue text-paper"
        >
          <svg
            clip-rule="evenodd"
            fill-rule="evenodd"
            stroke-linejoin="round"
            stroke-miterlimit="2"
            viewBox="0 0 24 24"
            width="25px"
            height="25px"
          >
            <path
              d="m22 7c0-.478-.379-1-1-1h-14c-.62 0-1 .519-1 1v14c0 .621.52 1 1 1h14c.478 0 1-.379 1-1zm-14.5.5h13v13h-13zm-5.5 7.5v2c0 .621.52 1 1 1h2v-1.5h-1.5v-1.5zm13.369-5s-2.136.002-4.616.003c-.414 0-.75.336-.75.75-.001 2.479-.003 4.612-.003 4.612 0 .41.335.745.75.745.414 0 .75-.334.75-.745v-2.804l4.439 4.439c.293.293.768.293 1.061 0s.293-.768 0-1.061l-4.439-4.439h2.808c.409 0 .741-.336.741-.75s-.333-.75-.741-.75zm-11.869.637v3.363h-1.5v-3.363zm0-4.637v3.637h-1.5v-3.637zm11.5-4v1.5h1.5v1.5h1.5v-2c0-.478-.379-1-1-1zm-10 0h-2c-.62 0-1 .519-1 1v2h1.5v-1.5h1.5zm4.5 1.5h-3.5v-1.5h3.5zm4.5 0h-3.5v-1.5h3.5z"
              fill-rule="nonzero"
            />
          </svg>
        </button>
      </div>
      <span className={`${showErrorText ? "" : "hidden"} text-red select-none`}>
        invalid api key!
      </span>
      <p className="mt-1 text-center phone:w-3/4 text-sm">
        wk-similar does not need any permissions other than{" "}
        <span className="bg-paper text-red rounded p-1">all_data:read</span>{" "}
        <br />
        i.e., practicing here doesn't affect your wanikani account!
      </p>
      <button
        onClick={handleSubmit}
        disabled={key.length === 0}
        className="btn btn-lg bg-pink text-paper font-mono font-extrabold text-4xl m-5 hover:bg-pink hover:drop-shadow-md"
      >
        行こう
      </button>
      <p style={{ display: showErrorText ? "" : "none" }}>invalid api key!</p>
    </div>
  );
};
