import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const userSlice = createSlice({
  name: "user",
  initialState: {
    apiKey: "",
    level: 0,
    kanjiIds: [],
  },
  reducers: {
    setApiKey: (state, action) => {
      state.apiKey = action.payload;
    },
    setLevel: (state, action) => {
      state.level = action.payload;
    },
    setKanjiIds: (state, action) => {
      state.kanjiIds = action.payload;
    },
  },
});

export const { setApiKey, setLevel, setKanjiIds } = userSlice.actions;

export const initStudyByLevel = (selectedLevels: string) => {
  return async (dispatch: any, useState: any) => {
    const res = await axios.get(
      `https://api.wanikani.com/v2/assignments?levels=${selectedLevels}`,
      {
        headers: {
          Authorization: `Bearer ${useState().user.apiKey}`,
        },
      }
    );
    const kanjiIds = res.data.data.map(
      (assignment: any) => assignment.subject_id
    );
    dispatch(setKanjiIds(kanjiIds));
  };
};

export default userSlice.reducer;
// holds api token, user stats (accuracy, etc), user settings (theme, etc), user account info (level, etc)
