import axios from "axios";
import React, { useState } from "react";
import { FC } from "react";

interface WelcocmeProps {
  hide: () => void;
}

export const Welcome: FC<WelcocmeProps> = ({ hide }) => {
  const [key, setKey] = useState(""); // api key

  const handleSubmit = () => {
    // try to fetch user data
    axios
      .get("https://api.wanikani.com/v2/user", {
        headers: {
          Authorization: `Bearer ${key}`,
        },
      })
      .then((res) => {
        if (res.status === 200) {
          // success
        } else {
          // fail
          console.log();
        }
      });
    // if success, save api key to local storage + redux store
    // if fail, show error message
  };

  return (
    <div>
      <p>enter api key to start:</p>
      <input type="text" value={key} onChange={(e) => setKey(e.target.value)} />
    </div>
  );
};
