interface ModeSelectProps {}

export const ModeSelect: FC<ModeSelectProps> = () => {
  const [selectedLevel, setSelectedLevel] = useState<number>(0);
  const [selectedSrs, setSelectedSrs] = useState([
    {
      name: "apprentice",
      color: "pink",
      selected: false,
      id: 5,
    },
    {
      name: "guru",
      color: "purple",
      selected: false,
      id: 6,
    },
    {
      name: "master",
      color: "[#304ed1]",
      selected: false,
      id: 7,
    },
    {
      name: "enlightened",
      color: "blue",
      selected: false,
      id: 8,
    },
    {
      name: "burned",
      color: "text",
      selected: false,
      id: 9,
    },
  ]);

  const userLevel = useSelector(
    (state: { user: { level: number } }) => state.user.level
  );
  const apiKey = useSelector(
    (state: { user: { apiKey: string } }) => state.user.apiKey
  );

  const dispatch = useDispatch();

  useEffect(() => {
    if (userLevel === 0) {
      axios
        .get("https://api.wanikani.com/v2/user", {
          headers: {
            Authorization: `Bearer ${apiKey}`,
          },
        })
        .then((res) => {
          if (res.status === 200) {
            if (res.data.data.level === 0) {
              console.log("no level data available");
            }
            dispatch(setLevel(res.data.data.level));
            setSelectedLevel(res.data.data.level);
          }
        })
        .catch((err) => {
          console.log(err);
        });
    }
  }, []);

  const genLevelRange = () => {
    const range = [];
    for (let i = 1; i <= selectedLevel; i++) {
      range.push(i);
    }

    console.log(range.toString());
  };

  const genSrsSelection = () => {
    console.log(selectedSrs);
  };

  return (
    <div className="bg-background w-full h-screen flex justify-center items-center flex-col font-body">
      <div className="w-1/3 mx-auto flex flex-col gap-10">
        <div className="collapse">
          <input type="radio" name="mode-select" />
          <div className="collapse-title bg-pink text-paper cursor-pointer">
            <p className="text-2xl font-bold text-center pl-6">
              study by level
            </p>
          </div>
          <div className="collapse-content bg-paper text-text flex justify-center items-center flex-col">
            <p className="pt-5">select level range to study from:</p>
            <div className="flex justify-center items-center gap-2">
              <p className="w-3 text-lg">0</p>
              <input
                type="range"
                step="1"
                min="1"
                max={userLevel}
                value={selectedLevel}
                onChange={(e) => setSelectedLevel(parseInt(e.target.value))}
                id="level-range"
                className="w-52 range [--range-shdw:#00aaff]"
              />
              <p className="w-5 font-body text-lg">{selectedLevel}</p>
            </div>
            <button
              onClick={genLevelRange}
              className="btn btn-outline font-mono text-lg bg-paper text-text w-36 mx-auto mt-5"
            >
              勉強しよう
            </button>
          </div>
        </div>
        {/* srs_stages
              9 -> burned
              8 -> enlightened
              7 -> master
              6 -> guru
              5 -> apprentice */}
        <div className="collapse">
          <input type="radio" name="mode-select" />
          <div className="collapse-title bg-pink text-paper cursor-pointer">
            <p className="text-2xl font-bold text-center pl-6">
              study by srs stage
            </p>
          </div>
          <div className="collapse-content bg-paper text-text flex justify-center items-center flex-col">
            <p className="pt-5 pb-1">select stages to study from:</p>
            <div className="form-control flex flex-col justify-center items-center gap-2">
              {selectedSrs.map((srs, i) => (
                <label
                  key={i}
                  className={`label cursor-pointer bg-${srs.color} rounded p-3`}
                >
                  <input
                    type="checkbox"
                    className="checkbox"
                    checked={srs.selected}
                    onChange={(e) => {
                      const newSrs = [...selectedSrs];
                      newSrs[i].selected = e.target.checked;
                      setSelectedSrs(newSrs);
                    }}
                  />
                  <span className="label-text pl-2 w-36 text-paper">
                    {srs.name}
                  </span>
                </label>
              ))}
              {/* //   <label className="label cursor-pointer bg-pink rounded p-3">
//   <input
//     type="checkbox"
//     className="checkbox"
//     checked={selectedSrs.apprentice}
//     onChange={(e) =>
//       setSelectedSrs({
//         ...selectedSrs,
//         apprentice: e.target.checked,
//       })
//     }
//   />
//   <span className="label-text pl-2 w-36 text-paper">
//     apprentice
//   </span>
// </label> */}
              {/* <SrsCheckbox
                name="apprentice"
                checked={selectedSrs.apprentice}
                toggleSelected={() =>
                  setSelectedSrs({
                    ...selectedSrs,
                    apprentice: !selectedSrs.apprentice,
                  })
                }
                color="pink"
              />
              <SrsCheckbox
                name="guru"
                checked={selectedSrs.guru}
                toggleSelected={() =>
                  setSelectedSrs({
                    ...selectedSrs,
                    guru: !selectedSrs.guru,
                  })
                }
                color="purple"
              />
              <SrsCheckbox
                name="master"
                checked={selectedSrs.master}
                toggleSelected={() =>
                  setSelectedSrs({
                    ...selectedSrs,
                    master: !selectedSrs.master,
                  })
                }
                color="[#304ed1]"
              />
              <SrsCheckbox
                name="enlightened"
                checked={selectedSrs.enlightened}
                toggleSelected={() =>
                  setSelectedSrs({
                    ...selectedSrs,
                    enlightened: !selectedSrs.enlightened,
                  })
                }
                color="blue"
              />
              <SrsCheckbox
                name="burned"
                checked={selectedSrs.burned}
                toggleSelected={() =>
                  setSelectedSrs({
                    ...selectedSrs,
                    burned: !selectedSrs.burned,
                  })
                }
                color="text"
              /> */}
            </div>
            <button
              onClick={() => console.log(selectedSrs)}
              className="btn btn-outline font-mono text-lg bg-paper text-text w-36 mx-auto mt-5"
            >
              勉強しよう
            </button>
          </div>
        </div>
        <div className="collapse">
          <input type="radio" name="mode-select" />
          <div className="collapse-title bg-pink text-paper cursor-pointer">
            <p className="text-2xl font-bold text-center pl-6">study random</p>
          </div>
          <div className="collapse-content bg-paper text-text">content 3</div>
        </div>
      </div>
    </div>
  );
};

