import React, { useState } from "react";
import { FC } from "react";

interface ModeSelectProps {}

export const ModeSelect: FC<ModeSelectProps> = () => {
  const [selectedMode, setSelectedMode] = useState<string | null>(null);

  return (
    <div className="bg-background w-full h-screen flex flex-col justify-center items-center font-body text-text gap-5">
      <button
        className="btn bg-pink text-paper"
        disabled={selectedMode !== "level" && selectedMode !== null}
        onClick={() => setSelectedMode("level")}
      >
        study by level
      </button>
      <div>penis</div>
      <div className="divider w-1/3 mx-auto"></div>
      <button
        id="srs-mode-btn"
        className="btn bg-pink text-paper"
        disabled={selectedMode !== "srs" && selectedMode !== null}
        onClick={() => setSelectedMode("srs")}
      >
        study by srs stage
      </button>
      <div className="divider w-1/3 mx-auto"></div>
      <button
        className="btn bg-pink text-paper"
        disabled={selectedMode !== "random" && selectedMode !== null}
        onClick={() => setSelectedMode("random")}
      >
        study random
      </button>
      <p onClick={() => setSelectedMode(null)}>cancel</p>
    </div>
  );
};

