import React from "react";
import { FC } from "react";

interface KanjiInfoProps {
  children: React.ReactNode;
  radicals?: any;
  childIndex?: number;
  forceDisplay?: boolean;
}

/**
 * Displays information about a kanji
 * @param {React.ReactNode} children - kanji button to be wrapped by tooltip
 * @param {any} radicals - array of radicals that make up kanji
 * @param {number} childIndex - index of kanji button in array of kanji answerbuttons
 */
export const KanjiInfo: FC<KanjiInfoProps> = ({
  children,
  radicals,
  childIndex = 0,
  forceDisplay = false,
}) => {
  return (
    <div
      className={`tooltip ${forceDisplay ? "tooltip-open" : ""} tooltip-${
        childIndex % 2 === 0 ? "left" : "right"
      }`}
      data-tip="hello"
      onClick={() => console.log(radicals, childIndex)}
    >
      {children}
    </div>
  );
};
