import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const userSlice = createSlice({
  name: "user",
  initialState: {
    apiKey: "",
    level: 0,
    kanjiIds: [],
  },
  reducers: {
    setApiKey: (state, action) => {
      state.apiKey = action.payload;
    },
    setLevel: (state, action) => {
      state.level = action.payload;
    },
    setKanjiIds: (state, action) => {
      state.kanjiIds = action.payload;
    },
  },
});

export const { setApiKey, setLevel, setKanjiIds } = userSlice.actions;

export const initStudyByLevel = (selectedLevels: string, apiKey: string) => {
  return async (dispatch: any) => {
    const levels = selectedLevels.split(",");

    levels.forEach((level) => {
      axios
        .get(`https://api.wanikani.com/v2/subjects?levels=${level}`, {
          headers: {
            Authorization: `Bearer ${apiKey}`,
          },
        })
        .then((res) => {
          const kanjiIds = res.data.data.map((kanji: any) => kanji.id);
          dispatch(setKanjiIds(kanjiIds));
        });
    });
  };
};

export default userSlice.reducer;
// holds api token, user stats (accuracy, etc), user settings (theme, etc), user account info (level, etc)
