  //       },
  //     })
  //     .then((response) => {
  //       setKanji(response.data.data);
  //       response.data.data.visually_similar_subject_ids.map((id) => {
  //         axios
  //           .get(`https://api.wanikani.com/v2/subjects/${id}/`, {
  //             headers: {
  //               Authorization: "Bearer 736760db-bfa6-4465-91e0-24c14138736c",
  //             },
  //           })
  //           .then((response) => {
  //             setSimilar((prev) => [...prev, response.data.data]);
  //           });
  //       });
  //     });
  // };

  /**
   * Gets a list of kanji that are available to study
   */
  const getAvailableKanji = () => {
    axios
      .get(
        "https://api.wanikani.com/v2/assignments?started=true&subject_types=kanji",
        {
          headers: {
            Authorization: `Bearer ${apiKey}`,
          },
        }
      )
      .then((res) => {
        console.log(res.data);
        const totalAvailableKanji = res.data.total_count;

        if (totalAvailableKanji > 0) {
          // setAvailableKanjiCountTotal(res.data.total_count);
          // pick a random kanji from 0 to totalAvailableKanji (or 500, whichever is smaller)
          getRandomKanji(res.data.data);
        } else {
          console.log("no kanji available");
        }
      });
  };

  const getRandomKanji = (availableKanji) => {
    // choose a random kanji index from the first 500 available kanji
    const randomIndex = Math.floor(
      Math.random() *
        (availableKanji.length > 500 ? 500 : availableKanji.length)
    );

    console.log("getrando", availableKanji[randomIndex].data.subject_id);

    axios
      .get(
        `https://api.wanikani.com/v2/subjects/${availableKanji[randomIndex].data.subject_id}`,
        {
          headers: {
            Authorization: `Bearer ${apiKey}`,
          },
        }
      )
      .then((res) => {
        console.log("getrando", res.data);
        console.log(res.data.data.visually_similar_subject_ids);
        // if kanji doesn't have more than 2 visually similar kanji, get a new one
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    const key = JSON.parse(localStorage.getItem("apiKey"))?.apiKey;

    if (key) {
      dispatch(setApiKey(key));
      setShowWelcome(false);
    }
  }, []);

  // useEffect(() => {
  //   if (apiKey) {
  //     getAvailableKanji();
  //   }
  // }, [apiKey]);

  // study by level
  // study by srs stage ?
  //                    -- study by random --

  return (
    <div className="App">
      {/* <button onClick={() => getKanji(479)}>get</button>
      <h1>{kanji.characters}</h1>
      <div onClick={() => console.log(similar)}>
        {similar?.map((kanji) => (
          <p key={kanji.characters} onClick={() => console.log(kanji)}>
            {kanji.characters}
          </p>
        ))}
      </div> */}
      {showWelcome ? <Welcome hide={() => setShowWelcome(false)} /> : null}
      <button onClick={() => getAvailableKanji()}>study</button>
    </div>
  );
}

export default App;
