import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import axios from "axios";

interface ReturnedKanjiData {
  // data: {
  //   available_at: string | null;
  //   burned_at: string | null;
  //   created_at: string | null;
  //   hidden: boolean;
  //   passed_at: string | null;
  //   resurrected_at: string | null;
  //   srs_stage: number;
  //   started_at: string | null;
  //   subject_id: number;
  //   subject_type: string;
  //   unlocked_at: string | null;
  // };
  // data_updated_at: string;
  // id: number;
  // object: string;
  // url: string;
  data: {
    amalgamation_subject_ids: number[];
    auxiliary_meanings: [
      {
        meaning: string;
        type: string;
      }
    ];
    characters: string;
    component_subject_ids: number[];
    created_at: string | null;
    document_url: string;
    hidden_at: string | null;
    lesson_position: number;
    level: number;
    meaning_hint: string;
    meaning_mnemonic: string;
    meanings: [
      {
        meaning: string;
        primary: boolean;
        accepted_answer: boolean;
      }
    ];
    reading_hint: string;
    reading_mnemonic: string;
    readings: [
      {
        reading: string;
        primary: boolean;
        accepted_answer: boolean;
        type: string;
      }
    ];
    slug: string;
    visually_similar_subject_ids: number[];
  };
  data_updated_at: string;
  id: number;
  object: "kanji";
  url: string;
}

const userSlice = createSlice({
  name: "user",
  initialState: {
    apiKey: "",
    level: 0,
    kanjiIds: [],
  },
  reducers: {
    setApiKey: (state, action: PayloadAction<string>) => {
      state.apiKey = action.payload;
    },
    setLevel: (state, action: PayloadAction<number>) => {
      state.level = action.payload;
    },
    setKanjiIds: (state, action: PayloadAction<any>) => {
      state.kanjiIds = action.payload;
    },
  },
});

export const { setApiKey, setLevel, setKanjiIds } = userSlice.actions;

export const initStudyByLevel = (
  selectedLevels: string | null = null,
  selectedStages: string | null = null
) => {
  return async (dispatch: any, useState: any) => {
    let url = "https://api.wanikani.com/v2/subjects?types=kanji&started=true";
    if (selectedLevels) {
      url += `&levels=${selectedLevels}`;
    } else if (selectedStages) {
      url += `&srs_stages=${selectedStages}`;
    }

    console.log("url", url);
    const apiKey = useState().user.apiKey;

    // recursively fetch all pages of kanji ids
    const fetchIds = async (url: string | null) => {
      if (url) {
        await axios
          .get(url, {
            headers: {
              Authorization: `Bearer ${apiKey}`,
            },
          })
          .then((res) => {
            console.log("fetched", res.data);
            // dispatch(
            //   setKanjiIds(
            //     useState().user.kanjiIds.concat(
            //       res.data.data.map(
            //         (kanji: ReturnedKanjiData) => {
            //           if (kanji.data.visually_similar_subject_ids)
            //         }
            //       )
            //     )
            //   )
            // );

            // if (
            //   res.data.pages.per_page > res.data.total_count &&
            //   res.data.pages.next_url
            // ) {
            //   console.log("fetching next page");
            //   fetchIds(res.data.pages.next_url);
            // }
          })
          .catch((err) => {
            console.log(err);
          });
      }
    };

    // await fetchIds(
    //   `https://api.wanikani.com/v2/assignments?subject_types=kanji&started=true&levels=${selectedLevels}`
    // );
    await fetchIds(url);
  };
};

export default userSlice.reducer;
// holds api token, user stats (accuracy, etc), user settings (theme, etc), user account info (level, etc)

